mapper.select:
select a.company_id companyId,a.company_name companyName,m.ori_name oriName,m.file_name fileName,m.path, DATE_FORMAT(m.create_time,'%Y-%m-%d') createTime, m.tax_dict taxDict,m.currency ,m.material_type_dict materialTypeDict, DATE_FORMAT(d.tax_period,'%Y-%m-%d') as taxPeriod, d.tax_application_id as detailId from (t_material m left join t_tax_application_detail d on m.id = d.tax_returns or m.id = d.payment_certificate or m.id = d.pre_tax_returns or m.id = d.other_upload ) left join t_tax_application a on d.tax_application_id = a.id where 1=1 and a.company_id != '' and m.tax_dict != ''
and a.company_id in
#{id}
and m.tax_dict in
#{id}
and m.material_type_dict = #{materialTypeDict}
and d.tax_period between str_to_date(#{startTime},'%Y-%m-%d %H:%i:%s') and str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
union select ta.company_id companyId,ta.company_name companyName,m.ori_name oriName,m.file_name fileName,m.path, DATE_FORMAT(m.create_time,'%Y-%m-%d') createTime, m.tax_dict taxDict,m.currency ,m.material_type_dict materialTypeDict,'taxPeriod', 'detailId' from t_material m left join t_tax_application ta on m.id = ta.financial_report where 1=1 and ta.company_id != '' and m.tax_dict!= ''
and ta.company_id in
#{id}
and m.tax_dict !='none'
and m.material_type_dict = #{materialTypeDict}
and m.create_time between str_to_date(#{startTime},'%Y-%m-%d %H:%i:%s') and str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
